INSERT INTO `elmundode`.`anecdota` (`idAnecdota`, `idBebe`, `fechaAnecdota`, `nombreAnecdota`, `lugarAnecdota`, `descripcionAnecdota`, `extraAnecdota`, `tipoExtra`) VALUES ( null, '1', '2020-09-16', 'Adi&oacute;s c&oacute;licos', 'Hogar, dulce hogar', 'Fue el primer d&iacute;a que no te toc&oacute; sufrir por los famosos c&oacute;licos del lactante y la primera vez que de las 3 a las 4 de la ma&ntilde;ana fue una hora feliz y no de llanto.', null, NULL), ( null, '1', '2021-04-13', 'Primera palabra', 'Casa de los abuelos', 'Pese a que todo el mundo cuenta que lo primero que dice un beb&eacute; es \'pap&aacute;\', t&uacute; fuiste a contracorriente y dijiste \'mam&aacute;\'.', NULL, NULL), ( null, '1', '2021-09-17', 'Primera profe prefe', 'Guarder&iacute;a', 'La primera profesora favorita que tuviste fue Ana. &iquest;Te acuerdas? Morena, de pelo rizo y con gafas rojas redondas. &iexcl;Te encantaba!', NULL, NULL);